import React, { Component } from 'react';

import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

import Button from '@material-ui/core/Button';

// card
import {Card, CardContent, CardActions} from '@material-ui/core/';
import {List, ListItem, ListItemIcon, ListItemText, ListSubheader} from '@material-ui/core/';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';

import PersonIcon from '@material-ui/icons/Person';

const useStyles = makeStyles({
  avatar: {
    margin: "10 0",
    width: 60,
    height: 60,
  },
  card: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

const capitalize = (s) => {
  if (typeof s !== 'string') return ''
  return s.charAt(0).toUpperCase() + s.slice(1)
}

export function HomePage(props) {
  const classes = useStyles();
  return(
      <Grid container justify="center" alignItems="center" spacing={5}>
        <Grid item xs={8}>
          <Avatar className={classes.avatar}>{props.user_meta.username?props.user_meta.username.slice(0, 3):null}</Avatar>
          <Typography  variant="h5" component="h2">
            {'Hello, '+ props.localize.welcome + ' ' + props.user_meta.firstname + ' ' + props.user_meta.lastname}
          </Typography>

          <Typography  variant="p" component="p">
            {"You can manage your personal infomation in this account page."}
          </Typography>
        </Grid>
        <Grid item xs={8}>
          <Card className={classes.homeCard}>
            <CardContent>
              <Typography  variant="h5" component="h2">
                {capitalize(props.localize.about_you)}
              </Typography>
              <List className={classes.root}>
                <ListItem>
                  <ListItemIcon>
                    <PersonIcon />
                  </ListItemIcon>
                  <ListItemText primary="userid: userid" />
                </ListItem>
              </List>
            </CardContent>
            <CardActions>
              <Button  color="primary">Learn More</Button>
            </CardActions>
          </Card>
        </Grid>

        <Grid item xs={8}>
          <Card className={classes.homeCard}>
            <CardContent>
              <Typography  variant="h5" component="h2">
                {props.localize.edit_my_account}
              </Typography>
            </CardContent>
            <CardActions>
              <Button  color="primary">{props.localize.edit}</Button>
            </CardActions>
          </Card>
        </Grid>

      </Grid>
  )
};
