import React, { Component } from 'react';
import './App.css';
import {classNames} from 'classnames';

import { withStyles } from '@material-ui/core/styles';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';

// card
import {Card, CardContent, CardActions} from '@material-ui/core/';

// color
import blue from '@material-ui/core/colors/blue';

// list
import {ListItem, ListItemIcon, ListItemText} from '@material-ui/core/';

// icons
import MenuIcon from '@material-ui/icons/Menu';
import AccountIcon from '@material-ui/icons/AccountCircle';
import CodeIcon from '@material-ui/icons/Code';
import HomeIcon from '@material-ui/icons/Home';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';



const styles = theme=> ({
  root: {
    flexGrow: 1,
  },
  headerBar: {
    backgroundColor: "white",
    boxShadow: "none"
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  },
  title: {
    display: 'block'
  },

  drawerList: {
    width: 250,
  },

  homeCard: {
    maxWidth: 300,
    margin: 20,
    display: "inline-block"
  }
})

const theme = createMuiTheme({
  palette: {
    primary: { main: blue[500] }, // Purple and green play nicely together.
    secondary: { main: '#11cb5f' }, // This is just green.A700 as hex.
  },

  typography: { useNextVariants: true },

  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  }

});

class App extends Component {
  constructor(props) {
    super(props);
    this.state= {
      langs: require('./langs.json')['zh'],
      DrawerOpened: false
    }
  }

  toggleDrawer = (bool)=> ()=> {
    this.setState({DrawerOpened: bool});
  }

  render() {
    console.log(this.props.classes);
    const {classes} = this.props;

    const sideList = (
      <div className={classes.drawerList}>
        <ListItem button>
          <ListItemText primary={'NoService '+this.state.langs.account}/>
        </ListItem>
        <ListItem button>
          <Avatar>M</Avatar>
          <ListItemText primary={"Mr. Robot"} />
        </ListItem>
        <Divider />
        <ListItem button>
          <ListItemIcon><HomeIcon/></ListItemIcon>
          <ListItemText primary={this.state.langs.home} />
        </ListItem>
        <ListItem button>
          <ListItemIcon><AccountIcon/></ListItemIcon>
          <ListItemText primary={this.state.langs.my_account} />
        </ListItem>
        <ListItem button>
          <ListItemIcon><ExitToAppIcon/></ListItemIcon>
          <ListItemText primary={this.state.langs.sign_out} />
        </ListItem>
        <Divider />
        <List>
          <ListItem button>
            <ListItemIcon><CodeIcon/></ListItemIcon>
            <ListItemText primary={this.state.langs.noshell} />
          </ListItem>
        </List>
      </div>
    )

    const cardlist = (
      <div>
        <Card className={classes.homeCard}>
          <CardContent>
            <Typography  variant="h5" component="h2">
              {this.state.langs.noshell}
            </Typography>

          </CardContent>
          <CardActions>
            <Button size="small" color="primary">Learn More</Button>
          </CardActions>
        </Card>
        <Card className={classes.homeCard}>
          <CardContent>
            <Typography  variant="h5" component="h2">
              {this.state.langs.noshell}
            </Typography>

          </CardContent>
          <CardActions>
            <Button size="small" color="primary">Learn More</Button>
          </CardActions>
        </Card>
      </div>
    )

    // const cardlist = null;


    return (
      <MuiThemeProvider theme={theme}>
        <div className={classes.root}>
          <AppBar className={classes.headerBar} position="static">
            <Toolbar>
              <IconButton className={classes.menuButton} aria-label="Open drawer" onClick={this.toggleDrawer(true)}>
                <MenuIcon />
              </IconButton>
              <Typography className={classes.title} variant="h6" component="h1" noWrap>
                {'NoService '+this.state.langs.account}
              </Typography>
            </Toolbar>
          </AppBar>
          <SwipeableDrawer
           open={this.state.DrawerOpened}
           onClose={this.toggleDrawer(false)}
           onOpen={this.toggleDrawer(true)}
          >
             {sideList}
          </SwipeableDrawer>
          {cardlist}
        </div>
      </MuiThemeProvider>
    );
  }
}

export default withStyles(styles)(App);
